const designFile = $('Watch Designs Folder').item.json;
const mockups = $input.item.json.data.mockups;

// Social media platforms and their optimal sizes
const platforms = [
  { name: 'Instagram-Post', size: 1080, ratio: '1:1' },
  { name: 'Instagram-Story', size: 1080, ratio: '9:16' },
  { name: 'Twitter-Post', size: 1200, ratio: '16:9' },
  { name: 'LinkedIn-Post', size: 1200, ratio: '1.91:1' }
];

const combinations = [];

for (const mockup of mockups) {
  for (const platform of platforms) {
    const smartObject = mockup.smart_objects[0];

    combinations.push({
      designName: designFile.name,
      designUrl: designFile.webViewLink,
      mockupName: mockup.name,
      mockupUuid: mockup.uuid,
      smartObjectUuid: smartObject.uuid,
      platform: platform.name,
      targetSize: platform.size,
      aspectRatio: platform.ratio,
      outputName: `${designFile.name.split('.')[0]}_${mockup.name}_${platform.name}`
    });
  }
}

return combinations.map(c => ({ json: c }));

const loopData = $('Loop Through Variants').item.json;
const renderResult = $('Render Mockup').item.json;
const editedImage = $input.item.json;

return [{
  json: {
    designName: loopData.designName,
    mockupName: loopData.mockupName,
    platform: loopData.platform,
    outputName: loopData.outputName,
    aspectRatio: loopData.aspectRatio,
    mockupUrl: renderResult.renderedImageUrl || null,
    finalImageData: editedImage.data,
    success: !!renderResult.renderedImageUrl
  }
}];

const allResults = $input.all();

// Group by platform for Buffer scheduling
const byPlatform = {
  'Instagram-Post': [],
  'Instagram-Story': [],
  'Twitter-Post': [],
  'LinkedIn-Post': []
};

allResults.forEach(r => {
  const platform = r.json.platform;
  if (byPlatform[platform]) {
    byPlatform[platform].push(r.json);
  }
});

const designName = allResults[0]?.json?.designName || 'Design';

return [{
  json: {
    designName: designName,
    totalVariants: allResults.length,
    successCount: allResults.filter(r => r.json.success).length,
    byPlatform: byPlatform,
    allUrls: allResults.filter(r => r.json.mockupUrl).map(r => r.json.mockupUrl)
  }
}];
