// Create combinations: each design Ã— each product template
const designs = $('Get New Designs').all();
const templates = $input.item.json.data.mockups;

const combinations = [];

for (const design of designs) {
  for (const template of templates) {
    // Find the main smart object (usually first one)
    const smartObject = template.smart_objects[0];

    combinations.push({
      designName: design.json.name,
      designUrl: design.json.webViewLink,
      templateName: template.name,
      templateUuid: template.uuid,
      smartObjectUuid: smartObject.uuid,
      productType: template.name.split('-')[0] // e.g., "TShirt-Mockup" -> "TShirt"
    });
  }
}

return combinations.map(c => ({ json: c }));

const loopData = $('Loop Through Combinations').item.json;
const renderResult = $input.item.json;

return [{
  json: {
    designName: loopData.designName,
    productType: loopData.productType,
    mockupUrl: renderResult.renderedImageUrl || null,
    success: !!renderResult.renderedImageUrl,
    error: renderResult.error || null
  }
}];

const allResults = $input.all();

// Group by design name
const byDesign = {};
allResults.forEach(r => {
  const design = r.json.designName;
  if (!byDesign[design]) byDesign[design] = [];
  byDesign[design].push(r.json);
});

// Create CSV rows
const csvRows = ['Design Name,Product Type,Mockup URL,Status'];
allResults.forEach(r => {
  csvRows.push(`${r.json.designName},${r.json.productType},${r.json.mockupUrl || 'FAILED'},${r.json.success ? 'SUCCESS' : 'FAILED'}`);
});

return [{
  json: {
    totalCombinations: allResults.length,
    successCount: allResults.filter(r => r.json.success).length,
    failedCount: allResults.filter(r => !r.json.success).length,
    byDesign: byDesign,
    csvContent: csvRows.join('\n')
  }
}];
